mapper.select:
		select * from articles where author_id = #{authorId} and isopen = 'on'
		order by article_id desc
        limit #{startRow},#{size}

		select * from articles where author_id = #{authorId}
		order by article_id desc
        limit #{startRow},#{size}

mapper.update:
update articles
set likecount = #{likecount,jdbcType=INTEGER}
where article_id = #{articleId,jdbcType=INTEGER}

update articles
set dislike = #{dislike,jdbcType=INTEGER}
where article_id = #{articleId,jdbcType=INTEGER}

update articles
set reading = reading + 1
where article_id = #{articleId,jdbcType=INTEGER}
